mapper.select:
SELECT no, title, content, hit, reg_date, user_no, name FROM( SELECT ROWNUM rn, no, title, content, hit, reg_date, user_no, name FROM ( SELECT b.no, b.title, b.content, b.hit, to_char(b.reg_date, 'YYYY-MM-DD') AS reg_date, b.user_no, s.name FROM board b, users s WHERE b.user_no = s.no
and b.title LIKE '%'||#{keyword}||'%'
ORDER BY b.no DESC ) ) where rn >=#{start} and rn <=#{end}

mapper.update:

UPDATE 	board
SET		title = #{title},
		content = #{content}
WHERE	no = #{no}


update board
set hit= hit+1 
where no = #{no}
